    -6L), class = "data.frame"), content = charToRaw(" <!DOCTYPE html>\r\n<html lang=\"en-US\">\r\n    <head>\r\n        <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\"/>\r\n        <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0, user-scalable=1\"/>\r\n        <meta http-equiv=\"content-type\" content=\"text/html;charset=UTF-8\" />\r\n        <meta http-equiv=\"cache-control\" content=\"no-cache,no-store\"/>\r\n        <meta http-equiv=\"pragma\" content=\"no-cache\"/>\r\n        <meta http-equiv=\"expires\" content=\"-1\"/>\r\n        <meta name='mswebdialog-title' content='Connecting to The World Bank Group'/>\r\n\r\n        <title>Sign In</title>\r\n        <script type='text/javascript'>\r\n//<![CDATA[\r\nfunction LoginErrors(){this.userNameFormatError = 'Enter your user ID in the format \\u0026quot;domain\\\\user\\u0026quot; or \\u0026quot;user@domain\\u0026quot;.'; this.passwordEmpty = 'Enter your password.'; this.passwordTooLong = 'Password is too long (\\u0026gt; 128 characters).';}; var maxPasswordLength = 128;\r\n//]]>\r\n</script>\r\n\r\n<script type='text/javascript'>\r\n//<![CDATA[\r\n// Copyright (c) Microsoft Corporation.  All rights reserved.\r\nfunction InputUtil(errTextElementID, errDisplayElementID) {\r\n\r\n    if (!errTextElementID)  errTextElementID = 'errorText'; \r\n    if (!errDisplayElementID)  errDisplayElementID = 'error'; \r\n\r\n    this.hasFocus = false;\r\n    this.errLabel = document.getElementById(errTextElementID);\r\n    this.errDisplay = document.getElementById(errDisplayElementID);\r\n};\r\nInputUtil.prototype.canDisplayError = function () {\r\n    return this.errLabel && this.errDisplay;\r\n}\r\nInputUtil.prototype.checkError = function () {\r\n    if (!this.canDisplayError){\r\n        throw new Error ('Error element not present');\r\n    }\r\n    if (this.errLabel && this.errLabel.innerHTML) {\r\n        this.errDisplay.style.display = '';        \r\n        var cause = this.errLabel.getAttribute('for');\r\n        if (cause) {\r\n            var causeNode = document.getElementById(cause);\r\n            if (causeNode && causeNode.value) {\r\n                causeNode.focus();\r\n                this.hasFocus = true;\r\n            }\r\n        }\r\n    }\r\n    else {\r\n        this.errDisplay.style.display = 'none';\r\n    }\r\n};\r\nInputUtil.prototype.setInitialFocus = function (input) {\r\n    if (this.hasFocus) return;\r\n    var node = document.getElementById(input);\r\n    if (node) {\r\n        if ((/^\\s*$/).test(node.value)) {\r\n            node.focus();\r\n            this.hasFocus = true;\r\n        }\r\n    }\r\n};\r\nInputUtil.prototype.setError = function (input, errorMsg) {\r\n    if (!this.canDisplayError) {\r\n        throw new Error('Error element not present');\r\n    }\r\n    input.focus();\r\n\r\n    if (errorMsg) {\r\n        this.errLabel.innerHTML = errorMsg;\r\n    }\r\n    this.errLabel.setAttribute('for', input.id);\r\n    this.errDisplay.style.display = '';\r\n};\r\nInputUtil.makePlaceholder = function (input) {\r\n    var ua = navigator.userAgent;\r\n\r\n    if (ua != null && \r\n        (ua.match(/MSIE 9.0/) != null || \r\n         ua.match(/MSIE 8.0/) != null ||\r\n         ua.match(/MSIE 7.0/) != null)) {\r\n        var node = document.getElementById(input);\r\n        if (node) {\r\n            var placeholder = node.getAttribute(\"placeholder\");\r\n            if (placeholder != null && placeholder != '') {\r\n                var label = document.createElement('input');\r\n                label.type = \"text\";\r\n                label.value = placeholder;\r\n                label.readOnly = true;\r\n                label.style.position = 'absolute';\r\n                label.style.borderColor = 'transparent';\r\n                label.className = node.className + ' hint';\r\n                label.tabIndex = -1;\r\n                label.onfocus = function () { this.nextSibling.focus(); };\r\n\r\n                node.style.position = 'relative';\r\n                node.parentNode.style.position = 'relative';\r\n                node.parentNode.insertBefore(label, node);\r\n                node.onkeyup = function () { InputUtil.showHint(this); };\r\n                node.onblur = function () { InputUtil.showHint(this); };\r\n                node.style.background = 'transparent';\r\n\r\n                node.setAttribute(\"placeholder\", \"\");\r\n                InputUtil.showHint(node);\r\n            }\r\n        }\r\n    }\r\n};\r\nInputUtil.focus = function (inputField) {\r\n    var node = document.getElementById(inputField);\r\n    if (node) node.focus();\r\n};\r\nInputUtil.hasClass = function(node, clsName) {\r\n    return node.className.match(new RegExp('(\\\\s|^)' + clsName + '(\\\\s|$)'));\r\n};\r\nInputUtil.addClass = function(node, clsName) {\r\n    if (!this.hasClass(node, clsName)) node.className += \" \" + clsName;\r\n};\r\nInputUtil.removeClass = function(node, clsName) {\r\n    if (this.hasClass(node, clsName)) {\r\n        var reg = new RegExp('(\\\\s|^)' + clsName + '(\\\\s|$)');\r\n        node.className = node.className.replace(reg, ' ');\r\n    }\r\n};\r\nInputUtil.showHint = function (node, gotFocus) {\r\n    if (node.value && node.value != '') {\r\n        node.previousSibling.style.display = 'none';\r\n    }\r\n    else {\r\n        node.previousSibling.style.display = '';\r\n    }\r\n};\r\nInputUtil.updatePlaceholder = function (input, placeholderText) {\r\n    var node = document.getElementById(input);\r\n    if (node) {\r\n        var ua = navigator.userAgent;\r\n        if (ua != null &&\r\n            (ua.match(/MSIE 9.0/) != null ||\r\n            ua.match(/MSIE 8.0/) != null ||\r\n            ua.match(/MSIE 7.0/) != null)) {\r\n            var label = node.previousSibling;\r\n            if (label != null) {\r\n                label.value = placeholderText;\r\n            }\r\n        }\r\n        else {\r\n            node.placeholder = placeholderText;\r\n        }\r\n    }\r\n};\r\n\r\n//]]>\r\n</script>\r\n\r\n\r\n        \r\n        <link rel=\"stylesheet\" type=\"text/css\" href=\"/adfs/portal/css/style.css?id=D74D4D6943F32AE6F7F11D14D601DBB0E1A58919176EE512150366B6279AAF99\" /><style>.illustrationClass {background-image:url(/adfs/portal/illustration/illustration.jpg?id=5DA7246011C8476011CFA582D2B4960602870D6D5D5F0CDBD3A689485E2C6F5E);}</style>\r\n\r\n    </head>\r\n    <body dir=\"ltr\" class=\"body\">\r\n    <div id=\"noScript\" style=\"position:static; width:100%; height:100%; z-index:100\">\r\n        <h1>JavaScript required</h1>\r\n        <p>JavaScript is required. This web browser does not support JavaScript or JavaScript in this web browser is not enabled.</p>\r\n        <p>To find out if your web browser supports JavaScript or to enable JavaScript, see web browser help.</p>\r\n    </div>\r\n    <script type=\"text/javascript\" language=\"JavaScript\">\r\n         document.getElementById(\"noScript\").style.display = \"none\";\r\n    </script>\r\n    <div id=\"fullPage\">\r\n        <div id=\"brandingWrapper\" class=\"float\">\r\n            <div id=\"branding\"></div>\r\n        </div>\r\n        <div id=\"contentWrapper\" class=\"float\">\r\n            <div id=\"content\">\r\n                <div id=\"header\">\r\n                    <img class='logoImage' id='companyLogo' src='/adfs/portal/logo/logo.png?id=EA715AFB65860C3E8F9A0D42B9830101436DFD71EFC0CF4FD18778ACC536BC47' alt='The World Bank Group'/>\r\n                </div>\r\n                <div id=\"workArea\">\r\n                    \r\n    <div id=\"authArea\" class=\"groupMargin\">\r\n        \r\n        \r\n        \r\n    <div id=\"loginArea\">        \r\n        <div id=\"loginMessage\" class=\"groupMargin\">Sign in with your organizational account</div>\r\n\r\n        <form method=\"post\" id=\"loginForm\" autocomplete=\"off\" novalidate=\"novalidate\" onKeyPress=\"if (event && event.keyCode == 13) Login.submitLoginRequest();\" action